<div class="ml-6 thrust-table-density">
    <a id='collapse' class="mr-1 cursor-pointer o30"    onclick="$('table').removeClass('expanded').addClass('condensed');    $(this).removeClass('o30'); $('#expand').addClass('o30'); $('#expand').addClass('o30');">@icon(list)</a>
    <a id='normal'   class="mr-1 cursor-pointer"        onclick="$('table').removeClass('condensed expanded'); $(this).removeClass('o30'); $('#collapse').addClass('o30'); $('#expand').addClass('o30');">@icon(list)</a>
    <a id='expand'   class="cursor-pointer o30"    onclick="$('table').removeClass('condensed').addClass('expanded'); $(this).removeClass('o30'); $('#collapse').addClass('o30'); $('#normal').addClass('o30');">@icon(th-list)</a>
</div>
